package main_test

import (
	"bytes"
	"fmt"
	"testing"

	"github.com/ethereum/go-ethereum/common/hexutil"
	"github.com/ethereum/go-ethereum/rlp"
)

func Test_Proof_RLP_Encode(t *testing.T) {
	d1 := hexutil.MustDecode("0x2080")
	d2 := hexutil.MustDecode("")

	ds := make([][]byte, 2)
	ds = append(ds, d1)
	ds = append(ds, d2)

	var buff bytes.Buffer
	err := rlp.Encode(&buff, ds)
	if err != nil {
		t.Error(err)
	}

	t.Log(fmt.Sprintf("rlp:[%x]", buff.Bytes()))
}
